        <script>
            $(function () {
                $('.btn-toggle-mapview').click(function (e) {
                    e.preventDefault();
                    $('body').addClass('show-map');
                });
                $('.btn-toggle-listview').click(function (e) {
                    e.preventDefault();
                    $('body').removeClass('show-map');
                });
                $('.search-filter-scrollbar').slimScroll({
                    height: '100%'
                });
                $('#show_filter').click(function (e) {
                    e.preventDefault();
                    $('.search').toggleClass('show-search-filter');
                });
                $('.filter-close').click(function (e) {
                    e.preventDefault();
                    $('.search').toggleClass('show-search-filter');
                });
                $("#range_age").ionRangeSlider({
                    min: 18,
                    max: 60,
                    from: 30
                });
                $("#range_height").ionRangeSlider({
                    type: "single",
                    min: 55,
                    max: 84,
                    from: 65,
                    prettify: function(num) {
                        var feet = Math.floor(num / 12);
                        var inches = num % 12;
                        return feet + "'" + inches + '"'; // output something like 6'7"
                    }
                });
                $("#range_rate").ionRangeSlider({
                    min: 1,
                    max: 1500,
                    from: 30
                });
            });           
          
            $(function () {
                $('.input-group.date').datepicker({
                    autoclose: true
                });
            });
        </script>
               
